import { registerRoutesFromSelector } from '../routify'
import { RoutingPageMixin } from './RoutingPageMixin'

export const MainPageMixin = (base) => {
  return class Base extends RoutingPageMixin(base) {

    firstUpdated () {
      super.firstUpdated()

      if (this.constructor.routifySelector) {
        registerRoutesFromSelector(this.shadowRoot, this.constructor.routifySelector)
      }
    }

    shouldUpdate () {
      return true
    }
  }
}
